import React from "react";
import * as c from './Constants.js'
import "./CreateGlobalPayloadComponent.css"
import { Dropdown, Input } from 'semantic-ui-react'

class CreateGlobalPayloadComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            payloadValue: null,
        }

    }

    render() {
        return (
            <div className="payload-container">
                <div className="payload-type">
                    <Dropdown placeholder='Select Type' fluid selection options={c.DECISIONTYPES} />
                </div>
                <div className="payload">
                    <Input icon='asl' placeholder='Payload Value' onChange={
                        (event, data) => {
                            this.setState({ payloadValue: data.value });
                        }} value={this.state.payloadValue} />
                </div>
                <div>
                    <button type="button" onClick={this.handleCreateTask}>
                        Create Task
                 </button>
                </div>
            </div >
        );
    }
}



export default CreateGlobalPayloadComponent;
