import React from "react";
import { Dropdown, Button, Input } from 'semantic-ui-react'

class ChangeGlobalPayloadComponent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            intPayloadOptions: [],
            stringPayloadOptions: [],
            isIntPayload: true,
            newPayload: "",
        }
    }

    componentDidMount() {
        const contract = this.props.drizzle.contracts.ContractCollaborationManager;
        const ccmState = this.props.drizzleState.contracts.ContractCollaborationManager;

        this.setIntegerPayloadOptions(contract, ccmState);
        this.setStringPayloadOptions(contract, ccmState);
    }

    setIntegerPayloadOptions(contract, ccmState) {
        contract.methods.getGlobalIntegerPayloadCount().call().then((count) => {
            let intPayloadOptions = [];
            let keys = [];
            for (let i = 0; i < count; i++) {
                keys.push(contract.methods.getGlobalIntPayloadValueById.cacheCall(i));

                let payload = ccmState.getGlobalIntPayloadValueById[keys[i]];
                if (payload) {
                    intPayloadOptions.push({ key: i, value: i, text: `ID: ${i} | Payload: ${payload.value}` });
                }
            }
            this.setState({ intPayloadOptions, intPayloadKeys: keys });
        });
    }

    setStringPayloadOptions(contract, ccmState) {
        contract.methods.getGlobalStringPayloadCount().call().then((count) => {
            let stringPayloadOptions = [];
            this.setState({ stringPayloadOptions });
            let keys = [];
            for (let i = 0; i < count; i++) {
                keys.push(contract.methods.getGlobalStringPayloadValueById.cacheCall(i));
                let payload = ccmState.getGlobalStringPayloadValueById[keys[i]];
                if (payload) {
                    stringPayloadOptions.push({ key: i, value: i, text: `ID: ${i} | Payload: ${payload.value}` });
                }
            }
            this.setState({ stringPayloadOptions, stringPayloadKeys: keys });
        });
    }

    changePayload() {
        const contract = this.props.drizzle.contracts.ContractCollaborationManager;

        if (this.state.isIntPayload) {
            this.changeIntPayload(contract)
        } else {
            this.changeStringPayload(contract);
        }
    }

    changeIntPayload(contract) {
        let payloadID = this.state.selectedIntPayload;
        let newPayload = Number(this.state.newPayload);
        console.log("oldID", payloadID, "newPayload:", newPayload);

        contract.methods.changeGlobalIntPayload.cacheSend(payloadID, newPayload, {
            from: this.props.defaultAccount,
            gas: 600000
        });

    }

    changeStringPayload(contract) {
        let newPayload = Number(this.state.newPayload);
        let payloadID = this.state.selectedStringPayload;
        console.log("oldID", payloadID, "newPayload:", newPayload);

        contract.methods.changeGlobalStringPayload.cacheSend(payloadID, newPayload, {
            from: this.props.defaultAccount,
            gas: 600000
        });
    }

    getStringPayloadOptions() {
        let stringPayloadOptions = [];
        let keys = [];
        for (let i = 0; i < this.state.stringPayloadKeys; i++) {
            keys.push(contract.methods.getGlobalStringPayloadValueById.cacheCall(i));
            let payload = ccmState.getGlobalStringPayloadValueById[keys[i]];
            if (payload) {
                stringPayloadOptions.push({ key: i, value: i, text: `ID: ${i} | Payload: ${payload.value}` });
            }
        }
    }

    renderButtonGroup() {
        return (<label>
            <Button.Group>
                <Button
                    type="button"
                    color={this.state.isIntPayload ? 'blue' : null}
                    onClick={() => {
                        this.setState({ isIntPayload: true });
                    }}>Int</Button>
                <Button.Or />
                <Button

                    type="button"
                    color={!this.state.isIntPayload ? 'blue' : null}
                    onClick={(a, b) => {
                        this.setState({ isIntPayload: false });
                    }}>String</Button>
            </Button.Group>
        </label>);
    }

    render() {
        return (
            <div>
                {this.renderButtonGroup()}
                {
                    this.state.isIntPayload &&
                    <Dropdown placeholder='Select Global Integer Payload' onChange={
                        (event, data) => {
                            this.setState({ selectedIntPayload: data.value });
                        }} floating selection options={this.state.intPayloadOptions} value={this.state.selectedIntPayload} />
                }

                {
                    !this.state.isIntPayload &&
                    < Dropdown placeholder='Select Global String Payload' onChange={
                        (event, data) => {
                            this.setState({ selectedStringPayload: data.value });
                        }} floating selection options={this.state.stringPayloadOptions} value={this.state.selectedStringPayload} />
                }

                <Input placeholder='New Payload' onChange={
                    (event, data) => {
                        this.setState({ newPayload: data.value });
                    }} value={this.state.newPayload} />

                <button type="button" onClick={this.changePayload.bind(this)}>Change Payload</button>
            </div>
        );
    }
}
export default ChangeGlobalPayloadComponent
