import React from "react";
import { Table, Icon, Popup } from 'semantic-ui-react'
import * as c from './Constants.js'
import Tasks from "./Tasks"
import "./TaskTableComponent.css"

class TaskTableComponent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            getTasksDataKey: null,
            tasksData: [],
            getTaskByIdDataKeys: [],
            taskOptions: [],
        }
        this.first = true;
    }

    getTaskEnumById(id) {
        let res;
        c.TASKTYPES.forEach(taskType => {
            if (Number(taskType.value) === Number(id)) {
                res = taskType.text;
            }
        });
        return res;
    }

    getDecisionEnumById(id) {
        let res;
        c.DECISIONTYPES.forEach(decisionType => {
            if (Number(decisionType.value) === Number(id)) {
                res = decisionType.text;
            }
        });
        return res;
    }

    formatTaskIds(reqs) {
        let reqString = "";
        reqs.forEach(req => {
            reqString += req + ", "
        });
        return reqString.slice(0, reqString.length - 2)
    }

    formatTaskStatus(status) {
        if (status) {
            return <Icon name='checkmark' />
        }
        return <Icon name='attention' />
    }

    getDecisionOptions(taskData) {
        console.log("this is taskdata", taskData);

        if (taskData.decisionType) {
            return (
                <Popup key={taskData.id} trigger={
                    <Table.Row negative={!taskData.status} positive={taskData.status}>
                        <Table.Cell>{taskData.id}</Table.Cell>
                        <Table.Cell>{taskData.description}</Table.Cell>
                        <Table.Cell>{this.getTaskEnumById(taskData.tasktype)}</Table.Cell>
                        <Table.Cell>{taskData.resource}</Table.Cell>
                        <Table.Cell>{this.formatTaskIds(taskData.requirements)}</Table.Cell>
                        <Table.Cell>{this.formatTaskStatus(taskData.status)}</Table.Cell>
                        <Table.Cell>{"true"}</Table.Cell>
                    </Table.Row>
                }>
                    <Table celled>
                        <Table.Header>
                            <Table.Row >
                                <Table.HeaderCell>Decision Type</Table.HeaderCell>
                                <Table.HeaderCell>Competitors</Table.HeaderCell>
                                <Table.HeaderCell>Decision Completed</Table.HeaderCell>
                                <Table.HeaderCell>Gateway Type</Table.HeaderCell>
                                <Table.HeaderCell>Operator</Table.HeaderCell>
                                <Table.HeaderCell>Global Payload ID</Table.HeaderCell>
                                <Table.HeaderCell>Local Value</Table.HeaderCell>
                            </Table.Row>
                        </Table.Header>
                        <Table.Body>
                            <Table.Row >
                                <Table.Cell>{this.getDecisionEnumById(taskData.decisionType)}</Table.Cell>
                                <Table.Cell>{(taskData.competitors.length === 0) ? this.formatTaskIds(taskData.competitors) : "---"}</Table.Cell>
                                <Table.Cell>{(taskData.decisionCompleted) ? "true" : "false"}</Table.Cell>
                                <Table.Cell>{taskData.gatewayType}</Table.Cell>
                                <Table.Cell>{taskData.operator}</Table.Cell>
                                <Table.Cell>{taskData.integerOperants.idtoglobalintegerpayload}</Table.Cell>
                                <Table.Cell>{taskData.integerOperants.local}</Table.Cell>
                            </Table.Row>
                        </Table.Body>
                    </Table>
                </Popup>
            );
        } else {
            return (
                <Table.Row key={taskData.id} negative={!taskData.status} positive={taskData.status}>
                    <Table.Cell>{taskData.id}</Table.Cell>
                    <Table.Cell>{taskData.description}</Table.Cell>
                    <Table.Cell>{this.getTaskEnumById(taskData.tasktype)}</Table.Cell>
                    <Table.Cell>{taskData.resource}</Table.Cell>
                    <Table.Cell>{this.formatTaskIds(taskData.requirements)}</Table.Cell>
                    <Table.Cell>{this.formatTaskStatus(taskData.status)}</Table.Cell>
                    <Table.Cell>{"false"}</Table.Cell>
                </Table.Row>
            );
        }
    }

    taskDataCallback(index, taskData) {
        let options = this.state.taskOptions;
        options.push(this.getDecisionOptions(taskData));
        this.setState({ taskOptions: options });
    }

    createTableContentPromise() {
        const contract = this.props.drizzle.contracts.ContractCollaborationManager;
        Tasks.getAllTasksWithDecisionPromise(contract, this.taskDataCallback.bind(this));
    }

    componentDidMount() {
        this.createTableContentPromise();
    }

    render() {
        return (
            <div className="task-table">
                <Table celled>
                    <Table.Header>
                        <Table.Row>
                            <Table.HeaderCell>Task Id</Table.HeaderCell>
                            <Table.HeaderCell>Description</Table.HeaderCell>
                            <Table.HeaderCell>Type</Table.HeaderCell>
                            <Table.HeaderCell>Owner</Table.HeaderCell>
                            <Table.HeaderCell>Requirements</Table.HeaderCell>
                            <Table.HeaderCell>Status</Table.HeaderCell>
                            <Table.HeaderCell>Decision Type</Table.HeaderCell>
                        </Table.Row>
                    </Table.Header>

                    <Table.Body>

                        {this.state.taskOptions}

                    </Table.Body>
                </Table>
            </div>
        );
    }
}

export default TaskTableComponent;
