import React from "react";
import { Input, Dropdown } from 'semantic-ui-react'
import './Collaborator.css';

class Collaborators extends React.Component {
    state = {
        stackId: null,
        collaborator: null
    };

    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.setValue = this.setValue.bind(this);
    }

    handleChange(event) {

        this.setState({ collaborator: event.target.value });
    }


    setValue = value => {
        const { drizzle, drizzleState } = this.props;
        const contract = drizzle.contracts.ContractCollaborationManager;
        console.log(this.state.collaborator);
        // let drizzle know we want to call the `set` method with `value`

        const stackId = contract.methods.addCollaborator.cacheSend(this.state.collaborator, "HARTCODE", {
            from: drizzleState.accounts[0],
            gas: 600000
        });

        // save the `stackId` for later reference
        this.setState({ stackId });
    }

    getTxStatus = () => {
        // get the transaction states from the drizzle state
        const { transactionStack } = this.props.drizzleState;
        //console.log(this.props.drizzleState);
        // get the transaction hash using our saved `stackId`
        const txHash = transactionStack[this.state.stackId];

        // if transaction hash does not exist, don't display anything
        if (!txHash) return null;

        // otherwise, return the transaction status
        return `Transaction status: ${this.props.drizzleState}`;
    }

    getCollaboratorsDropDown(collaborators) {
        let options = []
        if (collaborators) {
            collaborators.forEach(collaborator => {
                options.push({ text: collaborator });
            });
        }
        return options;
    }


    componentDidMount() {
        const { drizzle } = this.props;
        const contract = drizzle.contracts.ContractCollaborationManager;

        // let drizzle know we want to watch the `myString` method
        const dataKey = contract.methods.getCollaborators.cacheCall();

        //const collaborators = contract.methods["collaboratorAddressArray"].cacheCall();
        // save the `dataKey` to local component state for later reference
        this.setState({ dataKey });
    }

    render() {
        // get the contract state from drizzleState
        const { ContractCollaborationManager } = this.props.drizzleState.contracts;

        // using the saved `dataKey`, get the variable we're interested in
        const collaborators = ContractCollaborationManager.getCollaborators[this.state.dataKey];
        const collaboratorOptions = this.getCollaboratorsDropDown(collaborators.value);
        // if it exists, then we display its value

        return (
            <div className="add-collaborater-container">
                <button type="button" onClick={this.setValue}>
                    Add Collborator
                 </button>

                <Input placeholder="Collaborator" onChange={this.handleChange} />
                <Dropdown inline options={collaboratorOptions} defaultValue={collaboratorOptions[0].value} />
                {/* <p>
                    Collaborators are: {collaborators && collaborators.value}
                </p> */}
            </div>
        );


    }
}

export default Collaborators;
