import React from "react";
import { Table } from 'semantic-ui-react'

class TaskTableComponent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            stackId: null,
            dataKeyGetTasks: null,
            dataKey: null,
            tasksStatus: [],
            testkey: null,
            dataKeysTaskData: []
        }
    }
    createTableContent(tasksObject) {
        const { ContractCollaborationManager } = this.props.drizzleState.contracts;
        const { drizzle } = this.props;
        const contract = drizzle.contracts.ContractCollaborationManager;
        const cells = []
        if (tasksObject) {
            const taskIds = tasksObject.value;

            for (let index = 0; index < taskIds.length; index++) {
                cells.push(<Table.Row key={taskIds[index]}><Table.Cell>{taskIds[index]}</Table.Cell></Table.Row>);
            }
        }
        return (cells);
    }

    shouldComponentUpdate(nextProps, nextState) {

        const contract = this.props.drizzle.contracts.ContractCollaborationManager;
        const { ContractCollaborationManager } = this.props.drizzleState.contracts;

        const tasksObject = ContractCollaborationManager.getTasks[this.state.dataKey];
        if (!tasksObject) return false;
        console.log(tasksObject);

        let dataKeysTaskData = [];
        tasksObject.value.forEach(taskId => {
            console.log(taskId);

            dataKeysTaskData.push(contract.methods.getTaskById.cacheCall(taskId))
        });


        this.setState({
            dataKeysTaskData: dataKeysTaskData
        });
        if (dataKeysTaskData === nextState.dataKeysTaskData) {
            return true;
        }
        return false;
    }


    componentDidMount() {
        const contract = this.props.drizzle.contracts.ContractCollaborationManager;
        const { ContractCollaborationManager } = this.props.drizzleState.contracts;

        // let drizzle know we want to watch the `myString` method
        const dataKey = contract.methods.getTasks.cacheCall();

        //const collaborators = contract.methods["collaboratorAddressArray"].cacheCall();
        // save the `dataKey` to local component state for later reference
        this.setState({ dataKey });
    }

    render() {

        const { ContractCollaborationManager } = this.props.drizzleState.contracts;
        const tasksObject = ContractCollaborationManager.getTasks[this.state.dataKey];
        const newCells = this.createTableContent(tasksObject);
        return (
            <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Task Id</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>

                    {newCells}

                </Table.Body>
            </Table>
        );
    }
}

export default TaskTableComponent;
