import React from "react";
import * as c from './Constants.js'
import "./CreateGlobalPayloadComponent.css"
import { Dropdown, Input, Popup, Button } from 'semantic-ui-react'

class CreateGlobalPayloadComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            payloadValue: "",
            decisionType: "",
            newId: "",
        }
    }

    createPayload() {
        const { drizzle } = this.props;
        const contract = drizzle.contracts.ContractCollaborationManager;
        let value = this.state.payloadValue;

        switch (this.state.decisionType) {
            case 0:
                this.createStringPayload(contract, String(value));
                break;
            case 1:
                this.createIntPayload(contract, Number(value));
                break;
        }
    }

    createStringPayload(contract, value) {
        console.log("WHYYYY");

        const id = contract.methods.createGlobalStringPayload.cacheSend(value, {
            from: this.props.defaultAccount,
            gas: 600000
        });
        this.setState({ newId: id })
    }

    createIntPayload(contract, value) {
        const id = contract.methods.createGlobalIntPayload.cacheSend(value, {
            from: this.props.defaultAccount,
            gas: 600000
        });
        this.setState({ newId: id });
    }

    render() {
        return (
            <div className="payload-container">
                <div className="payload-type">
                    <Dropdown placeholder='Select Type' fluid selection onChange={
                        (event, data) => {
                            this.setState({ decisionType: data.value });
                        }} fluid selection options={c.DECISIONTYPES} value={this.state.decisionType} />
                </div>
                <div className="payload">
                    <Input icon='money bill alternate outline' placeholder='Payload Value' onChange={
                        (event, data) => {
                            this.setState({ payloadValue: data.value });
                        }} value={this.state.payloadValue} />
                </div>

                <div className="payload-button">
                    <Popup
                        trigger={<button type="button" onClick={this.createPayload.bind(this)}>Create Payload</button>}
                        content={<p>ID:{this.state.newId}</p>}
                        on='click'
                        position='middleright'
                    />
                </div>



            </div >
        );
    }
}



export default CreateGlobalPayloadComponent;
