import React from "react";
import { Table } from 'semantic-ui-react'
import * as c from './Constants.js'
import "./TaskTableComponent.css"

class TaskTableComponent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            getTasksDataKey: null,
            tasksData: [],
            getTaskByIdDataKeys: []
        }
        this.first = true;
    }

    getTaskEnumById(id) {
        let res;
        c.TASKTYPES.forEach(taskType => {
            if (Number(taskType.value) === Number(id)) {
                res = taskType.text;
            }
        });
        return res;
    }

    formatTaskRequirements(reqs) {
        let reqString = "";
        reqs.forEach(req => {
            reqString += req + ", "
        });
        return reqString.slice(0, reqString.length - 2)
    }

    formatTaskStatus(status) {
        if (status) {
            return "Completed"
        }
        return "Not Completed"
    }

    createTableContent(tasksObject) {
        const cells = []
        const ccmState = this.props.drizzleState.contracts.ContractCollaborationManager;

        if (tasksObject) {
            const taskIds = tasksObject.value;

            for (let index = 0; index < taskIds.length; index++) {
                const tasksObject = ccmState.getTaskById[this.state.getTaskByIdDataKeys[index]];
                if (tasksObject) {

                    cells.push(<Table.Row key={taskIds[index]}>
                        <Table.Cell>{taskIds[index]}</Table.Cell>
                        <Table.Cell>{tasksObject.value.description}</Table.Cell>
                        <Table.Cell>{this.getTaskEnumById(tasksObject.value.tasktype)}</Table.Cell>
                        <Table.Cell>{tasksObject.value.stateowner}</Table.Cell>
                        <Table.Cell>{this.formatTaskRequirements(tasksObject.value.requirements)}</Table.Cell>
                        <Table.Cell>{this.formatTaskStatus(tasksObject.value.status)}</Table.Cell>


                    </Table.Row>);
                }
            }
        }
        return (cells);
    }

    componentDidMount() {
        const contract = this.props.drizzle.contracts.ContractCollaborationManager;
        const getTasksDataKey = contract.methods.getTasks.cacheCall();

        let ids = [];
        const getTaskByIdDataKeys = [];
        for (let i = 0; i < 200; i++) {
            ids.push(i);
        }
        ids.forEach(id => {
            getTaskByIdDataKeys.push(contract.methods.getTaskById.cacheCall(id));
        });


        this.setState({ getTasksDataKey, getTaskByIdDataKeys });
    }


    render() {
        const ccmState = this.props.drizzleState.contracts.ContractCollaborationManager;
        const tasksObject = ccmState.getTasks[this.state.getTasksDataKey];

        const newCells = this.createTableContent(tasksObject);

        return (
            <div className="task-table">
                <Table celled>
                    <Table.Header>
                        <Table.Row>
                            <Table.HeaderCell>Task Id</Table.HeaderCell>
                            <Table.HeaderCell>Description</Table.HeaderCell>
                            <Table.HeaderCell>Type</Table.HeaderCell>
                            <Table.HeaderCell>Owner</Table.HeaderCell>
                            <Table.HeaderCell>Requirements</Table.HeaderCell>
                            <Table.HeaderCell>Status</Table.HeaderCell>


                        </Table.Row>
                    </Table.Header>

                    <Table.Body>

                        {newCells}

                    </Table.Body>
                </Table>
            </div>
        );
    }
}

export default TaskTableComponent;
