import React from "react";
import Multiselect from 'multiselect-dropdown-react';

class CreateTask extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            stackId: null,
            taskTypes: ["Task", "Parallel", "OR", "EXCLUSIVE"],
            activity: '',
            address: '',
            tasktype: '',
            requirements: [],
            dataKey: null,
            tasks: null,
            reqoptions: []
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleRequirements = this.handleRequirements.bind(this);
        //this.handleCreateTask = this.handleCreateTask.bind(this);
    }


    componentDidMount() {
        const { drizzle } = this.props;
        const contract = drizzle.contracts.ContractCollaborationManager;
        const dataKey = contract.methods.getTasks.cacheCall();
        this.setState({ dataKey });
        //console.log(drizzle.web3);
    }

    updateOptions(options) {
        const optionlist = []
        if (options !== undefined) {
            for (var i = 0; i < options.length; i++) {
                let option = { value: parseInt(options[i]), name: options[i] }
                //console.log(option);
                optionlist.push(option)
            }
        }
        console.log("updateOptionlist() :", optionlist);

        return optionlist;
    }
    handleRequirements(req) {
        //console.log(req);
        this.setState({ requirements: req });
    }

    handleChange(evt) {
        this.setState({ [evt.target.name]: evt.target.value });
        //console.log(this.state.reqoptions);
    }


    handleCreateTask = () => {
        const { drizzle, drizzleState } = this.props;
        const contract = drizzle.contracts.ContractCollaborationManager;

        // let drizzle know we want to call the `set` method with `value`
        const stackId = contract.methods.createTask.cacheSend(this.state.activity,
            this.state.address, this.state.tasktype, this.state.requirements, {
                from: drizzleState.accounts[0],
                gas: 600000
            });
        //console.log(this.state.requirements);
        // save the `stackId` for later reference
        this.setState({ stackId });

    }

    render() {
        const { ContractCollaborationManager } = this.props.drizzleState.contracts;
        const tasks = ContractCollaborationManager.getTasks[this.state.dataKey];
        console.log("Tasks are:", tasks);

        let options = this.updateOptions(tasks.value);
        //const blub = [{ value: 10, name: "HARALD" }];
        return (
            <form>
                <button type="button" onClick={this.handleCreateTask}>
                    Create Task
                 </button>

                <label> Activity
                     <input type="text" name="activity" onChange={this.handleChange} />
                </label>
                <label> Address:
                     <input type="text" name="address" onChange={this.handleChange} />
                </label>
                <label> Tasktype:
                     <input type="text" name="tasktype" onChange={this.handleChange} />
                </label>
                <label> Requirements:
                   <Multiselect options={options} onSelectOptions={this.handleRequirements} />
                </label>
            </form>
        );
    }
}

export default CreateTask;
