import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";

import './App.css';
import Supervisor from "./SupervisorComponent";
import Collaborators from "./CollaboratorsComponent";
import CreateTaskComponent from "./CreateTaskComponent";
import TaskTable from "./TaskTableComponent";
import AccountSelectorComponent from './AccountSelectorComponent';
import { Menu } from 'semantic-ui-react'

class App extends Component {
  state = {
    loading: true,
    drizzleState: null,
    drizzle: null,
    defaultAccount: null
  };

  constructor(props, context) {
    super(props, context);
    React.createContext()
  }
  componentDidMount() {
    const { drizzle } = this.props;
    this.handleItemClick = this.handleItemClick.bind(this);
    // subscribe to changes in the store
    this.unsubscribe = drizzle.store.subscribe(() => {

      // every time the store updates, grab the state from drizzle
      const drizzleState = drizzle.store.getState();

      // check to see if it's ready, if so, update local component state
      if (drizzleState.drizzleStatus.initialized) {
        this.setState({ loading: false, drizzleState, drizzle });
      }
    });
  }

  componentWillUnmount() {
    this.unsubscribe();
  }

  handleItemClick = (e, { name }) => {
    this.setState({ activeItem: name });
  }

  renderMenu() {
    const { activeItem } = this.state;
    return (
      <Menu color={"grey"} inverted>
        <Menu.Item
          name="Create Task"
          color={'red'}
          active={activeItem === 'Create Task'}
          onClick={this.handleItemClick}
          as={Link}
          to="/create_task">
        </Menu.Item >
        <Menu.Item
          name="Add Collaborator"
          color={'red'}
          active={activeItem === 'Add Collaborator'}
          onClick={this.handleItemClick}
          as={Link}
          to="/add_collaborator">
        </Menu.Item >
        <Menu.Item
          name="Tasks"
          color={'red'}
          active={activeItem === 'Tasks'}
          onClick={this.handleItemClick}
          as={Link}
          to="/">
        </Menu.Item >
      </Menu >
    );
  }

  render() {
    if (this.state.loading) return "Loading Drizzle...";
    return (
      <Router>
        <div className="App">
          <div className="Menu">
            {this.renderMenu()}
          </div>
          <div className="acc-selector">
            <AccountSelectorComponent drizzle={this.state.drizzle} drizzleState={this.state.drizzleState} onAccountChanged={(selectedAccount) => {
              this.setState({ defaultAccount: selectedAccount });
            }} />
          </div>
          <Switch>
            <Route path="/add_collaborator" render={() =>
              <div>
                <Supervisor
                  drizzle={this.state.drizzle}
                  drizzleState={this.state.drizzleState}
                />
                <Collaborators
                  drizzle={this.state.drizzle}
                  drizzleState={this.state.drizzleState}
                  defaultAccount={this.state.defaultAccount}
                />
              </div>} />

            <Route path="/create_task" render={() =>
              <div>
                <CreateTaskComponent
                  drizzle={this.state.drizzle}
                  drizzleState={this.state.drizzleState}
                  defaultAccount={this.state.defaultAccount}
                />
              </div>} />

            <Route exact path="/" render={() =>
              <TaskTable
                drizzle={this.state.drizzle}
                drizzleState={this.state.drizzleState}
              />
            } />
          </Switch>
        </div>
      </Router>
    );
  }

}

export default App;
