import React from "react";
import * as c from './Constants.js'
import { Dropdown } from 'semantic-ui-react'
import Tasks from "./Tasks"


class AddDecisionComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedTask: null,
            getTasksDataKey: "",
            getTaskByIdDataKeys: "",
            getPayloadsDataKey: "",
            getIntPayloadCountKey: "",
            getStringPayloadCountKey: ""
        }
        this.decisionTypes = c.DECISIONTYPES;
        this.operator = c.OPERATORS;
        this.gatewayTypes = c.GATEWAYTPYES
    }

    componentDidMount() {
        const contract = this.props.drizzle.contracts.ContractCollaborationManager;
        const getTasksDataKey = contract.methods.getTasks.cacheCall();
        const intPayloadCountKey = contract.methods.getGlobalIntegerPayloadCount.cacheCall();

        const getTaskCountKey = contract.methods.getTaskCount.cacheCall();
        const ccmState = this.props.drizzleState.contracts.ContractCollaborationManager;

        let taskCount = ccmState.getTaskCount[getTaskCountKey];
        taskCount = (taskCount) ? Number(taskCount.value) : 150;
        const getTaskByIdDataKeys = [];

        for (let i = 0; i <= taskCount; i++) {
            getTaskByIdDataKeys.push(contract.methods.getTaskById.cacheCall(i));
        }
        this.setIntegerPayloadOptions(contract, ccmState);
        this.setState({
            getTasksDataKey,
            getTaskByIdDataKeys,
            intPayloadCountKey,

        });
    }

    setIntegerPayloadOptions(contract, ccmState) {
        contract.methods.getGlobalIntegerPayloadCount().call().then((count) => {
            let intPayloadOptions = [];

            for (let i = 0; i < count; i++) {
                let key = contract.methods.getGlobalIntPayloadValueById.cacheCall(i);

                let payload = ccmState.getGlobalIntPayloadValueById[key];
                if (payload) {
                    intPayloadOptions.push({ key: i, value: payload.value, text: `ID: ${i} | Payload: ${payload.value}` });
                }
            }
            this.setState({ intPayloadOptions });
        });
    }

    setStringPayloadOptions(contract, ccmState) {
        contract.methods.getGlobalStringPayloadCount().call().then((count) => {
            let stringPayloadOptions = [];

            for (let i = 0; i < count; i++) {
                let key = contract.methods.getGlobalStringPayloadValueById.cacheCall(i);

                let payload = ccmState.getGlobalStringPayloadValueById[key];
                if (payload) {
                    stringPayloadOptions.push({ key: i, value: payload.value, text: `ID: ${i} | Payload: ${payload.value}` });
                }
            }
            this.setState({ stringPayloadOptions });
        });
    }

    getStringPayloadKeys(contract, ccmState, countKey) {
        let stringPayloadKeys = [];
        let count = ccmState.getGlobalStringPayloadCount[countKey];
        count = (count) ? count.value : 5;
        for (let i = 0; i < count; i++) {
            stringPayloadKeys.push(contract.methods.getGlobalStringPayloadValueById.cacheCall(i));
        }

        return stringPayloadKeys;
    }

    getTaskDropdownOptions() {
        let tasks = Tasks.getAllTasks(this.props.drizzleState, this.state.getTaskByIdDataKeys, this.state.getTasksDataKey);
        let options = [];

        tasks.forEach(task => {
            options.push({ key: task.id, value: task.id, text: `ID: ${task.id} | Description: ${task.description}` });
        });
        return options;
    }


    setIntpayloads() {
        if (this.state.intPayloadKeys) {
            const contract = this.props.drizzle.contracts.ContractCollaborationManager;
            this.setState({ intPayloads: [] });
            for (let i = 0; i < this.state.intPayloadKeys.length; i++) {
                contract.methods.getGlobalIntPayloadValueById(i).call().then((payload) => {
                    this.setState({ intPayloads: this.state.intPayloads.push({ payload, id: i }) })
                });

            }
        }

    }



    render() {
        let taskOptions = this.getTaskDropdownOptions();

        return (
            <form className="add-decision-form" >
                <label className="select-task-label">
                    <Dropdown placeholder='Select Task' onChange={
                        (event, data) => {
                            this.setState({ selectedTask: data.value });
                        }
                    } fluid selection options={taskOptions} value={this.state.selectedTask} />
                </label>

                <label className="select-payload-label">
                    <Dropdown placeholder='Select Global Payload' onChange={
                        (event, data) => {
                            this.setState({ selectedPayload: data.value });
                        }} fluid selection options={this.state.intPayloadOptions} value={this.state.selectedPayload} />
                </label>
                {/* 
                <label className="add-task-label">
                    <Dropdown placeholder='Requirements' onChange={
                        (event, data) => {
                            this.setState({ requirements: data.value });
                        }
                    }
                        fluid multiple selection options={newOptions} value={this.state.requirements} />
                </label> */}
                <button type="button" onClick={this.handleCreateTask}>
                    Add Decision
                 </button>
            </form >
        );
    }
}



export default AddDecisionComponent;
