class Tasks {
    constructor(drizzleState, drizzle) {
        this.drizzleState = drizzleState;
        const contract = this.props.drizzle.contracts.ContractCollaborationManager;

        this.getTasksDataKey = contract.methods.getTasks.cacheCall();
        this.getTaskCountKey = contract.methods.getTaskCount.cacheCall();

        let taskCount = ccmState.getTaskCount[getTaskCountKey];
        taskCount = (taskCount) ? Number(ccmState.getTaskCount[getTaskCountKey].value) : taskCount;
        this.getTaskByIdDataKeys = [];

        for (let i = 0; i <= taskCount; i++) {
            this.getTaskByIdDataKeys.push(contract.methods.getTaskById.cacheCall(i));
        }
    }
    static getTaskIds() {
        const ccmState = this.drizzleState.contracts.ContractCollaborationManager;
        const tasksObject = ccmState.getTasks[this.getTasksDataKey];

        if (tasksObject) {
            return tasksObject.value;
        }
        return [];
    }

    static getAllTasks() {
        let tasks = [];
        const ccmState = this.drizzleState.contracts.ContractCollaborationManager;
        const taskIds = this.getTaskIds(this.drizzleState, this.getTasksDataKey);

        if (taskIds) {
            for (let index = 0; index < taskIds.length; index++) {
                const tasksObject = ccmState.getTaskById[this.getTaskByIdDataKeys[index]];
                if (tasksObject) {
                    tasks.push({
                        description: tasksObject.value.description,
                        tasktype: tasksObject.value.tasktype,
                        stateowner: tasksObject.value.stateowner,
                        requirements: tasksObject.value.requirements,
                        status: tasksObject.value.status,
                        id: index,
                    });
                }
            }
        }
        return tasks;
    }
}
export default Tasks
