class Tasks {

    static getTaskIds(drizzleState, getTasksDataKey) {
        const ccmState = drizzleState.contracts.ContractCollaborationManager;
        const tasksObject = ccmState.getTasks[getTasksDataKey];

        if (tasksObject) {
            return tasksObject.value;
        }
        return [];
    }

    static getAllTasks(drizzleState, getTaskByIdDataKeys, getTasksDataKey) {
        let tasks = [];
        const ccmState = drizzleState.contracts.ContractCollaborationManager;
        const taskIds = this.getTaskIds(drizzleState, getTasksDataKey);

        if (taskIds) {
            for (let index = 0; index < taskIds.length; index++) {
                const tasksObject = ccmState.getTaskById[getTaskByIdDataKeys[index]];
                if (tasksObject) {
                    tasks.push({
                        description: tasksObject.value.description,
                        tasktype: tasksObject.value.tasktype,
                        resource: tasksObject.value.resource,
                        requirements: tasksObject.value.requirements,
                        status: tasksObject.value.status,
                        id: index,
                    });
                }
            }
        }
        return tasks;
    }

    static getAllTasksPromise(contract, taskCallback) {

        contract.methods.getTasks().call().then((tasks) => {
            console.log("Tasks", tasks);

            // for (let index = 0; index < ids; index++) {
            //     contract.methods.getTaskById(index).call().then((task) => {
            //         let result = {
            //             description: task.value.description,
            //             tasktype: task.value.tasktype,
            //             resource: task.value.resource,
            //             requirements: task.value.requirements,
            //             status: task.value.status,
            //             id: index,
            //         };
            //         taskCallback(index, result);
            //     });
            // }
        });
    }

    static getAllTasksWithDecision(contract, drizzleState, getTaskByIdDataKeys, getTasksDataKey, decisionDataCallback) {
        let tasks = [];
        const ccmState = drizzleState.contracts.ContractCollaborationManager;
        const taskIds = this.getTaskIds(drizzleState, getTasksDataKey);

        if (taskIds) {
            for (let index = 0; index < taskIds.length; index++) {
                const tasksObject = ccmState.getTaskById[getTaskByIdDataKeys[index]];

                if (tasksObject) {
                    tasks.push({
                        description: tasksObject.value.description,
                        tasktype: tasksObject.value.tasktype,
                        resource: tasksObject.value.resource,
                        requirements: tasksObject.value.requirements,
                        status: tasksObject.value.status,
                        id: index,
                    });
                    contract.methods.getIntegerDecision(index).call().then((decisionObject) => {
                        decisionDataCallback(index, decisionObject);
                    });
                }
            }
        }
        return tasks;
    }
}
export default Tasks
