import React from "react";
import { Table } from 'semantic-ui-react'
import * as c from './Constants.js'
import Tasks from "./Tasks"
import "./TaskTableComponent.css"

class TaskTableComponent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            getTasksDataKey: null,
            tasksData: [],
            getTaskByIdDataKeys: []
        }
        this.Tasks = null;
        this.first = true;
    }

    getTaskEnumById(id) {
        let res;
        c.TASKTYPES.forEach(taskType => {
            if (Number(taskType.value) === Number(id)) {
                res = taskType.text;
            }
        });
        return res;
    }

    formatTaskRequirements(reqs) {
        let reqString = "";
        reqs.forEach(req => {
            reqString += req + ", "
        });
        return reqString.slice(0, reqString.length - 2)
    }

    formatTaskStatus(status) {
        if (status) {
            return "Completed"
        }
        return "Not Completed"
    }

    createTableContent() {
        let cells = [];
        if (this.Tasks) {
            let tasks = this.Tasks.getAllTasks(this.props.drizzleState, this.state.getTaskByIdDataKeys, this.state.getTasksDataKey);
            tasks.forEach(task => {
                cells.push(<Table.Row key={task.id}>
                    <Table.Cell>{task.id}</Table.Cell>
                    <Table.Cell>{task.description}</Table.Cell>
                    <Table.Cell>{this.getTaskEnumById(task.tasktype)}</Table.Cell>
                    <Table.Cell>{task.stateowner}</Table.Cell>
                    <Table.Cell>{this.formatTaskRequirements(task.requirements)}</Table.Cell>
                    <Table.Cell error={!task.status} >{this.formatTaskStatus(task.status)}</Table.Cell>
                </Table.Row>);
            });
        }
        return (cells);

    }

    componentDidMount() {
        this.Tasks = new Tasks(this.props.drizzleState, this.props.drizzle);
        // const contract = this.props.drizzle.contracts.ContractCollaborationManager;
        // const getTasksDataKey = contract.methods.getTasks.cacheCall();
        // const getTaskCountKey = contract.methods.getTaskCount.cacheCall();
        // const ccmState = this.props.drizzleState.contracts.ContractCollaborationManager;

        // let taskCount = ccmState.getTaskCount[getTaskCountKey];
        // taskCount = (taskCount) ? Number(ccmState.getTaskCount[getTaskCountKey].value) : taskCount;
        // const getTaskByIdDataKeys = [];

        // for (let i = 0; i <= taskCount; i++) {
        //     getTaskByIdDataKeys.push(contract.methods.getTaskById.cacheCall(i));
        // }

        // this.setState({ getTasksDataKey, getTaskByIdDataKeys });
    }

    render() {
        const newCells = this.createTableContent();

        return (
            <div className="task-table">
                <Table celled>
                    <Table.Header>
                        <Table.Row>
                            <Table.HeaderCell>Task Id</Table.HeaderCell>
                            <Table.HeaderCell>Description</Table.HeaderCell>
                            <Table.HeaderCell>Type</Table.HeaderCell>
                            <Table.HeaderCell>Owner</Table.HeaderCell>
                            <Table.HeaderCell>Requirements</Table.HeaderCell>
                            <Table.HeaderCell>Status</Table.HeaderCell>
                        </Table.Row>
                    </Table.Header>

                    <Table.Body>

                        {newCells}

                    </Table.Body>
                </Table>
            </div>
        );
    }
}

export default TaskTableComponent;
