import React from "react";
import { Dropdown } from 'semantic-ui-react'
import './CollaboratorsComponent.css';

class CollaboratorsComponent extends React.Component {
    state = {
        stackId: null,
        collaborator: null
    };

    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.setValue = this.setValue.bind(this);

    }

    handleChange(event) {
        this.setState({ collaborator: event.target.value });
    }

    setValue = value => {
        const { drizzle, drizzleState } = this.props;
        const contract = drizzle.contracts.ContractCollaborationManager;
        // let drizzle know we want to call the `set` method with `value`

        const stackId = contract.methods.addCollaborator.cacheSend(this.state.collaborator, "HARTCODE", {
            from: drizzleState.accounts[0],
            gas: 600000
        });

        // save the `stackId` for later reference
        this.setState({ stackId });
    }

    componentDidMount() {
        const { drizzle } = this.props;
        const contract = drizzle.contracts.ContractCollaborationManager;

        // let drizzle know we want to watch the `myString` method
        const dataKey = contract.methods.getCollaborators.cacheCall();

        //const collaborators = contract.methods["collaboratorAddressArray"].cacheCall();
        // save the `dataKey` to local component state for later reference
        this.setState({ dataKey });
    }

    render() {

        return (
            <div className="add-collaborater-container">
                <div className="collaborator-input">
                    <Dropdown placeholder='TaskType' onChange={
                        (event, data) => {
                            this.setState({ taskType: data.value });
                        }} fluid selection options={this.state.drizzleState.accounts} />
                    {/* <Input fluid placeholder="Collaborator" onChange={this.handleChange} /> */}
                </div>
                <button type="button" onClick={this.setValue}>
                    Add Collborator
                 </button>
            </div>
        );
    }
}

export default CollaboratorsComponent;
