import React from "react";
import { Table } from 'semantic-ui-react'

class TaskTableComponent extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            getTasksDataKey: null,
        }
        this.first = true;
    }
    createTableContent(tasksObject) {
        const cells = []

        if (tasksObject) {
            const taskIds = tasksObject.value;

            for (let index = 0; index < taskIds.length; index++) {
                //let key = this.props.drizzle.contracts.ContractCollaborationManager.methods.getTaskById.cacheCall(taskIds[index]);
                cells.push(<Table.Row key={taskIds[index]}><Table.Cell>{taskIds[index]}</Table.Cell></Table.Row>);
            }
        }
        return (cells);
    }

    componentDidMount() {
        const contract = this.props.drizzle.contracts.ContractCollaborationManager;
        const getTasksDataKey = contract.methods.getTasks.cacheCall();

        this.setState({ getTasksDataKey });
    }

    componentDidUpdate(prevState) {
        console.log("what", this.state);
    }

    render() {
        const ccmState = this.props.drizzleState.contracts.ContractCollaborationManager;
        const tasksObject = ccmState.getTasks[this.state.getTasksDataKey];
        const newCells = this.createTableContent(tasksObject);

        return (
            <Table celled>
                <Table.Header>
                    <Table.Row>
                        <Table.HeaderCell>Task Id</Table.HeaderCell>
                    </Table.Row>
                </Table.Header>

                <Table.Body>

                    {newCells}

                </Table.Body>
            </Table>
        );
    }
}

export default TaskTableComponent;
