import React from "react";
import * as c from './Constants.js'
import { Dropdown, Button, Input } from 'semantic-ui-react'
import Tasks from "./Tasks"


class AddDecisionComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            selectedTask: null,
            getTasksDataKey: "",
            getTaskByIdDataKeys: "",
            getPayloadsDataKey: "",
            getIntPayloadCountKey: "",
            getStringPayloadCountKey: "",
            intPayloadOptions: [],
            stringPayloadOptions: [{ key: 1, value: 2, text: `ID: 0| Payload: ${3}` }],
            isIntDecision: true,
        }
    }

    componentDidMount() {
        const contract = this.props.drizzle.contracts.ContractCollaborationManager;
        const getTasksDataKey = contract.methods.getTasks.cacheCall();
        const intPayloadCountKey = contract.methods.getGlobalIntegerPayloadCount.cacheCall();

        const getTaskCountKey = contract.methods.getTaskCount.cacheCall();
        const ccmState = this.props.drizzleState.contracts.ContractCollaborationManager;

        let taskCount = ccmState.getTaskCount[getTaskCountKey];
        taskCount = (taskCount) ? Number(taskCount.value) : 150;
        const getTaskByIdDataKeys = [];

        for (let i = 0; i <= taskCount; i++) {
            getTaskByIdDataKeys.push(contract.methods.getTaskById.cacheCall(i));
        }
        this.setIntegerPayloadOptions(contract, ccmState);
        this.setStringPayloadOptions(contract, ccmState);
        this.setState({
            getTasksDataKey,
            getTaskByIdDataKeys,
            intPayloadCountKey,

        });
    }

    setIntegerPayloadOptions(contract, ccmState) {
        contract.methods.getGlobalIntegerPayloadCount().call().then((count) => {
            let intPayloadOptions = [];

            for (let i = 0; i < count; i++) {
                let key = contract.methods.getGlobalIntPayloadValueById.cacheCall(i);

                let payload = ccmState.getGlobalIntPayloadValueById[key];
                if (payload) {
                    intPayloadOptions.push({ key: i, value: payload.value, text: `ID: ${i} | Payload: ${payload.value}` });
                }
            }
            this.setState({ intPayloadOptions });
        });
    }

    setStringPayloadOptions(contract, ccmState) {
        contract.methods.getGlobalStringPayloadCount().call().then((count) => {
            let stringPayloadOptions = [];
            this.setState({ stringPayloadOptions });

            for (let i = 0; i < count; i++) {
                let key = contract.methods.getGlobalStringPayloadValueById.cacheCall(i);

                // contract.methods.getGlobalStringPayloadValueById(i).call().then((payload) => {
                //     console.log("payload!!!", payload);

                //     let a = []
                //     a = a.concat(this.state.stringPayloadOptions);
                //     console.log("asdfasdfasdfasdf", a);

                //     a = a.push({ key: payload, value: payload, text: `ID: ${i}| Payload: ${payload}` });
                //     this.setState({ stringPayloadOptions: a });
                //     //this.setState({ stringPayloadOptions: this.state.stringPayloadOptions.push({ key: payload, value: payload, text: `ID: 0| Payload: ${payload}` }) });

                // });

                let payload = ccmState.getGlobalStringPayloadValueById[key];
                if (payload) {
                    stringPayloadOptions.push({ key: i, value: payload.value, text: `ID: ${i} | Payload: ${payload.value}` });
                }
            }
            this.setState({ stringPayloadOptions });
        });
    }

    getTaskDropdownOptions() {
        let tasks = Tasks.getAllTasks(this.props.drizzleState, this.state.getTaskByIdDataKeys, this.state.getTasksDataKey);
        let options = [];

        tasks.forEach(task => {
            options.push({ key: task.id, value: task.id, text: `ID: ${task.id} | Description: ${task.description}` });
        });
        return options;
    }

    renderButtonGroup() {
        let bg = (<label>
            <Button.Group>
                <Button
                    type="button"
                    color={this.state.isIntDecision ? 'blue' : null}
                    onClick={() => {
                        this.setState({ isIntDecision: true });
                    }}>Int Decision</Button>
                <Button.Or />
                <Button
                    inverted
                    type="button"
                    color={!this.state.isIntDecision ? 'blue' : 'black'}
                    onClick={(a, b) => {
                        this.setState({ isIntDecision: false });
                    }}>String Decision</Button>
            </Button.Group>
        </label>);
        return bg;
    }

    render() {
        let taskOptions = this.getTaskDropdownOptions();

        return (
            <form className="add-decision-form" >
                <label className="select-task-label">
                    <Dropdown placeholder='Select Task' onChange={
                        (event, data) => {
                            this.setState({ selectedTask: data.value });
                        }
                    } fluid selection options={taskOptions} value={this.state.selectedTask} />
                </label>

                <div className="decision-global-op-local">
                    {this.renderButtonGroup()}

                    {this.state.isIntDecision && <label className="select-payload-label">
                        <Dropdown placeholder='Select Global Int Payload' onChange={
                            (event, data) => {
                                this.setState({ selectedIntPayload: data.value });
                            }} fluid selection options={this.state.intPayloadOptions} value={this.state.selectedIntPayload} />
                    </label>}

                    {!this.state.isIntDecision && <label className="select-payload-label">
                        < Dropdown placeholder='Select Global String Payload' onChange={
                            (event, data) => {
                                this.setState({ selectedStringPayload: data.value });
                            }} fluid selection options={this.state.stringPayloadOptions} value={this.state.selectedStringPayload} />
                    </label >}

                    <label className="select-operator-label">
                        <Dropdown placeholder='Select Operator' onChange={
                            (event, data) => {
                                this.setState({ selectedOperator: data.value });
                            }} fluid selection options={c.OPERATORS} value={this.state.selectedOperator} />
                    </label>

                    <label className="select-operator-label">
                        <Input placeholder='Local Value' onChange={
                            (event, data) => {
                                this.setState({ localOperand: data.value });
                            }} value={this.state.localOperand} />
                    </label>

                </div>

                {/* <button type="button" onClick={this.handleCreateTask}>
                    Add Decision
                 </button> */}
            </form >
        );
    }
}



export default AddDecisionComponent;
