import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

import './App.css';
import Supervisor from "./Supervisor";
import Collaborators from "./Collaborators";
import CreateTask from "./CreateTask";
import TaskTable from "./TaskTable";
import AccountSelectorComponent from './AccountSelectorComponent';
import { Header, Menu } from 'semantic-ui-react'
import HomeComponent from './HomeComponent';



class App extends Component {
  state = { loading: true, drizzleState: null, drizzle: null };

  componentDidMount() {
    const { drizzle } = this.props;
    this.handleItemClick = this.handleItemClick.bind(this);
    // subscribe to changes in the store
    this.unsubscribe = drizzle.store.subscribe(() => {

      // every time the store updates, grab the state from drizzle
      const drizzleState = drizzle.store.getState();

      // check to see if it's ready, if so, update local component state
      if (drizzleState.drizzleStatus.initialized) {
        this.setState({ loading: false, drizzleState, drizzle });
      }
    });
  }

  componentWillUnmount() {
    this.unsubscribe();
  }

  handleItemClick = (e, { name }) => {
    console.log(BrowserRouter);

    this.setState({ activeItem: name });
  }

  renderMenu() {
    const { activeItem } = this.state;
    return (
      <Menu color={"grey"} inverted>
        <Menu.Item
          color={'red'}
          name='add Task'
          active={activeItem === 'show Tasks'}
          onClick={this.handleItemClick}
          to="/createTask"
        />
        <Menu.Item
          color={'red'}
          name='add Collaborator'
          active={activeItem === 'add Collaborator'}
          onClick={this.handleItemClick}
        />
        <Menu.Item
          color={'red'}
          name='show Tasks'
          active={activeItem === 'add Task'}
          onClick={this.handleItemClick}
        />
      </Menu>
    );
  }

  render() {
    if (this.state.loading) return "Loading Drizzle...";
    return (
      <Router>
        <div className="App">
          <div className="Menu">
            {this.renderMenu()}
          </div>
          <Switch>
            <Route exact path="/" render={() => <HomeComponent drizzle={this.state.drizzle} drizzleState={this.state.drizzleState} />} />
            <Route exact path="/" render={() => <div className="acc-selector">
              <AccountSelectorComponent drizzle={this.state.drizzle} drizzleState={this.state.drizzleState} />
            </div>} />

            <Route path="/createTask" render={() => <CreateTask drizzle={this.state.drizzle} drizzleState={this.state.drizzleState} />} />

            <TaskTable
              drizzle={this.state.drizzle}
              drizzleState={this.state.drizzleState}
            />
          </Switch>
        </div>
      </Router>
    );
  }

}

export default App;
